mapper.select:
        select
id, subject_id, is_correct, created_by, created_time, update_by, update
        from subject_judge


                and id = #{id}


                and subject_id = #{subjectId}


                and is_correct = #{isCorrect}


                and created_by = #{createdBy}


                and created_time = #{createdTime}


                and update_by = #{updateBy}


                and update = #{update}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from subject_judge


        and id = #{id}


        and subject_id = #{subjectId}


        and is_correct = #{isCorrect}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update = #{update}
